package br.com.elvisassis.payments.controller;import br.com.elvisassis.payments.domain.Payment;import br.com.elvisassis.payments.domain.PaymentStatus;import br.com.elvisassis.payments.dto.PaymentRequest;import br.com.elvisassis.payments.facede.PaymentFacade;import br.com.elvisassis.payments.service.PaymentService;import lombok.RequiredArgsConstructor;import org.springframework.web.bind.annotation.*;import java.math.BigDecimal;import java.util.UUID;@RestController@RequestMapping("/payments")@RequiredArgsConstructorpublic class PaymentController {    private final PaymentFacade paymentFacade;    @PostMapping("/")    public Payment create(@RequestBody PaymentRequest request) {        return paymentFacade.createPayment(request);    }    @PutMapping("/{id}/status")    public Payment updateStatus(@PathVariable UUID id,                                @RequestParam PaymentStatus status) {        return paymentFacade.updateStatus(id, status);    }}