package br.com.elvisassis.payments.controllers;import br.com.elvisassis.payments.domain.Payment;import br.com.elvisassis.payments.dto.PaymentRequest;import br.com.elvisassis.payments.dto.PaymentResponse;import br.com.elvisassis.payments.facade.PaymentFacade;import lombok.RequiredArgsConstructor;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("/payments")@RequiredArgsConstructorpublic class PaymentController {    private final PaymentFacade paymentFacade;    @PostMapping("/")    public ResponseEntity<PaymentResponse> create(@RequestBody PaymentRequest request) {        var response = paymentFacade.processPayment(request);        return ResponseEntity.ok(response);    }}