package br.com.elvisassis.payments.domain;import jakarta.persistence.*;import lombok.Data;import java.math.BigDecimal;import java.util.UUID;@Entity@Table(name = "payments")@Datapublic class Payment {    @Id    @GeneratedValue(strategy = GenerationType.UUID)    private UUID id;    private BigDecimal amount;    @Enumerated(EnumType.STRING)    private PaymentStatus status;    private String provider;    public void markAsProcessing() {        if (this.status != PaymentStatus.CREATED) {            throw new IllegalStateException("Payment must be in CREATED status to be marked as PROCESSING.");        }        this.status = PaymentStatus.PROCESSING;    }    public void markAsApproved() {        this.status = PaymentStatus.APPROVED;        this.provider = provider;    }    public void markAsFailed() {        this.status = PaymentStatus.FAILED;        this.provider = provider;    }}