package br.com.elvisassis.payments.facade;import br.com.elvisassis.payments.domain.Payment;import br.com.elvisassis.payments.dto.PaymentRequest;import br.com.elvisassis.payments.dto.PaymentResponse;import br.com.elvisassis.payments.mapper.PaymentMapper;import br.com.elvisassis.payments.service.PaymentService;import br.com.elvisassis.payments.useCases.CreatePaymentUseCase;import org.springframework.stereotype.Component;import java.util.UUID;@Componentpublic class PaymentFacade {    private final PaymentService paymentService;    private final CreatePaymentUseCase createPaymentUseCase;    public PaymentFacade(PaymentService paymentService, CreatePaymentUseCase createPaymentUseCase) {        this.paymentService = paymentService;        this.createPaymentUseCase = createPaymentUseCase;    }    public PaymentResponse processPayment(PaymentRequest request) {        return createPaymentUseCase.execute(request);    }    public Payment findById(UUID id) {        return paymentService.findById(id);    }}