package br.com.elvisassis.payments.listener;import br.com.elvisassis.payments.event.PaymentApprovedEvent;import org.springframework.context.event.EventListener;import org.springframework.scheduling.annotation.Async;import org.springframework.stereotype.Component;@Componentpublic class PaymentApprovedListener {    @Async    @EventListener    public void handlePaymentApprovedEvent(PaymentApprovedEvent event) {        System.out.println(                "[EVENT] Pagamento aprovado: " + event.paymentId() +                        " Provider: " + event.provider()        );    }}