package br.com.elvisassis.payments.listener;import br.com.elvisassis.payments.event.PaymentFailedEvent;import org.springframework.context.event.EventListener;import org.springframework.scheduling.annotation.Async;import org.springframework.stereotype.Component;@Componentpublic class PaymentFailedListener {    @Async    @EventListener    public void handlePaymentFailedEvent(PaymentFailedEvent event) {        System.out.println(                "[EVENT] Pagamento falhou: " + event.paymentId() +                        " Provider: " + event.provider()        );    }}