package br.com.elvisassis.payments.mapper;import br.com.elvisassis.payments.domain.Payment;import br.com.elvisassis.payments.dto.PaymentRequest;import br.com.elvisassis.payments.dto.PaymentResponse;import java.util.UUID;public class PaymentMapper {    public PaymentMapper(UUID id, String amount, String status, String currency) {    }    public static PaymentResponse toDTO(Payment payment) {        return new PaymentResponse(                payment.getId(),                payment.getAmount(),                payment.getCurrency(),                payment.getMethod(),                payment.getStatus(),                payment.getExternalTransactionId(),                payment.getProvider()        );    }    public static Payment toEntity(PaymentRequest request) {        return Payment.create(                request.amount(),                request.currency(),                request.method(),                request.provider(),                request.externalTransactionId()        );    }}