package br.com.elvisassis.payments.resolver;import br.com.elvisassis.payments.exceptions.ProvideNotFoundException;import br.com.elvisassis.payments.provider.PaymentProvider;import org.springframework.stereotype.Component;import java.util.List;@Componentpublic class PaymentProviderResolver {    private final List<PaymentProvider> providers;    public PaymentProviderResolver(List<PaymentProvider> providers) {        this.providers = providers;    }    public PaymentProvider resolve(String providerName) {        return providers.stream()                .filter(p -> p.name().equalsIgnoreCase(providerName))                .findFirst()                .orElseThrow(() -> new ProvideNotFoundException("Provider not found"));    }}